import { useState } from "react";

const AddPetForm = ({postPet}) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [breed, setBreed] = useState("");
    const [age, setAge] = useState(0);

    const [isValid,setIsValid] = useState(null);

    const handleSubmit = (e) =>{
        e.preventDefault();
        handleValidation();
        console.log(isValid);

        if(isValid == false) {
            return
        } 

        if(isValid){
        const petDTO = {
            name,
            type,
            breed,
            age
        };

        postPet(petDTO);

       setName("");
       setType("");
       setBreed("");
       setAge(0);
        };
    };

    const handleValidation = () => {
        setIsValid(true);
        if(name == ""){
            setIsValid(false);
            return alert("Fill in name!")
        }
        if(type == ""){
            setIsValid(false);
            return alert("Fill in animal type!")
        }

        if(breed == ""){
            setIsValid(false);
            return alert("Fill in animal breed")
        }

        if(age == 0){
            setIsValid(false);
            return alert("Fill in age")
        }
    }

    return ( 
        <>
        <h3>Add pet:</h3>
        <form onSubmit={handleSubmit}>
            <input 
            type="text"
            placeholder="Pet Name"
            value={name}
            onChange={(e)=> setName(e.target.value)} />

            <input 
            type="text"
            placeholder="Type of Animal"
            value={type}
            onChange={(e)=> setType(e.target.value)} 
            />

            <input 
            type="text"
            placeholder="Breed"
            value={breed}
            onChange={(e)=> setBreed(e.target.value)} 
             />

             <input 
             type="number"
             min={0} 
             max={600}
             placeholder="Age"
             value={age}
            onChange={(e)=> setAge(e.target.value)} 
             />

             <input type="Submit" value="Submit"/>

        </form>

        </>
     );
}

export default AddPetForm;
